using System;
using System.Collections.Generic;
using System.Text;

namespace HD.Station.FoodOrder
{
    public static class ImageHelper
    {

        public static string GetBase64Image(byte[] bytes)
        {
            return bytes != null ? $"data:image/png;base64,{Convert.ToBase64String(bytes)}" : "data:image/png;base64,";
        }

    }
}
